def get_random_leading_spaces() -> int:
    """Returns a random indentation between 0 and 127.
    SPACE_PROBS was created to closesly mirror the indentation distribution in the original dataset.
    TODO also take into account the current indentation, and favour indentation levels close to that value.
    """
    return SPACE_PROBS[random.randrange(len(SPACE_PROBS))]


def _bytes_feature(value):
  """Returns a bytes_list from a string / byte."""
  if isinstance(value, type(tf.constant(0))):
    value = value.numpy() # BytesList won't unpack a string from an EagerTensor.
  return tf.train.Feature(bytes_list=tf.train.BytesList(value=[value]))


def _int64_feature(value):
    """Returns an int64_list from a bool / enum / int / uint."""
    return tf.train.Feature(int64_list=tf.train.Int64List(value=[value]))


def create_example(tokens: List[int], label_pre: int, label_post: int):
    tokens = np.array(tokens, dtype=np.uint16)
    # label = tf.keras.utils.to_categorical(label, num_classes=MAX_INDENTATION)
    feature = {
        # 'file_name': _bytes_feature(file_name.encode('utf-8')),
        'toks': _bytes_feature(tf.io.serialize_tensor(tokens)),
        'pre': _int64_feature(label_pre),
        'post': _int64_feature(label_post)
    }
    return tf.train.Example(features=tf.train.Features(feature=feature))


def trainable_file_in_dir(dir: Path) -> Generator[Path, None, None]:
    # Returns a generator that yields all the files in a directory that are trainable
    for file in dir.iterdir():
        if file.is_file() and file.suffix in TRAINABLE_FILE_TYPES:
            yield file


def split_list(lst, val):
    result = []
    temp_list = []
    for i in lst:
        temp_list.append(i)
        if i == val:
           result.append(temp_list)
           temp_list = []
    result.append(temp_list)
    return result


def create_sub_blocks(file: Path) -> Generator[Tuple[List[int], int, int], None, None]:
    global skipped
    with open(str(file), "rb") as f:
        raw  = f.read()
        tokens = []
        for i in range(0, len(raw), 2):
            tokens.append(int.from_bytes(raw[i:i+2], 'little'))
    lines = split_list(tokens, Tokenizer.newline_token())
    if len(lines) == 0: return
    for i in range(len(lines)-1):
        labelpre = Tokenizer.count_leading_spaces(lines[i])
        labelpost = Tokenizer.count_leading_spaces(lines[i+1])
        if labelpre >= MAX_INDENTATION or labelpost >= MAX_INDENTATION:
            skipped += 1
            continue
        new_spaces = get_random_leading_spaces()
        core = [Tokenizer.pre_token(), *Tokenizer.get_space_tokens(new_spaces), *lines[i][labelpre:], Tokenizer.post_token()]
        if len(core) > MAX_TOKENS:
            continue
        # Roughly the max offset if each line is just a newline, and we're at the start or end of a file
        for offset in range(1, MAX_TOKENS):
            before_index = i - offset
            # Skip the line directly after the core line, since that is the one we're predicting for
            after_index = i + offset + 1
            before = []
            after = []
            if before_index >= 0:
                before = lines[before_index]
            if after_index < len(lines):
                after = lines[after_index]
            space_after = MAX_TOKENS - len(core) - len(before)
            if space_after >= 0:
                core = [*before, *core, *after[:space_after]]
            elif len(core) != MAX_TOKENS:
                space_before = MAX_TOKENS - len(core)
                core = [*before[-space_before:], *core]
            if len(core) >= MAX_TOKENS or (before_index < 0 and after_index >= len(lines)):
                break
        # Core will never be more than MAX_TOKENS long, but it may be less, in which case we need to pad it
        core = Tokenizer.resize(core, MAX_TOKENS)
        yield core, labelpre, labelpost


